import React from 'react'
import exerciseImg from '../imagenes/exercise.png'
import circlesIMG from '../imagenes/circles.png'
import './styles/card.css'

class Card extends React.Component{
    render(){
        return (
            <div className="card mx-auto Fitness-Card" style={{backgroundImage: `url(${circlesIMG}), linear-gradient(to right, ${this.props.leftColor}, ${this.props.rightColor})`}}>
                <div className="card-body">
                    <div className="row center">
                        <div className="col-6">
                            <img src={this.props.img} alt="Ejercicio" className="float-right imgex"/>
                        </div>
                        <div className="col-6 Fitness-Card-Info">
                            <h1>{this.props.title}</h1>
                            <p>{this.props.description}</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Card;
